import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useRecoilState } from "recoil";
import styled from "styled-components";
import { accountAtom } from "../../../atoms/accountAtom";
import axios from "axios";

const Login = () => {
  const [id, setId] = useState("");
  const [password, setPassword] = useState("");
  const [asmlRequest, setAsmlRequest] = useState("");
  const [account, setAccount] = useRecoilState(accountAtom);
  const navigate = useNavigate();

  async function loginPost(id: string, password: string) {
    const response = await axios.post(
      "/sso/processAuthnResponse.do",
      {
        autoLogin: "Y",
        SAMLRequest:
          "",
        RelayState: null,
        returnPage: "/jsp/sso/ip/login_form.jsp",
        userAgent: "PC|MACINTEL|CHROME|114",
        relogin: "N",
        userId: id,
        password: password,
      },
      {
        headers: {
          "Content-Type": "application/x-www-form-urlencoded",
          Accept:
            "text/html,application/xhtml+xml,application/xml;q=0.9,image/avif,image/webp,image/apng,/;q=0.8,application/signed-exchange;v=b3;q=0.7",
        },
      }
    );
    console.log(response);
    const searchParams = new URLSearchParams(response.data);

    setAsmlRequest(searchParams.get("SAMLRequest"));
    console.log(searchParams.get("SAMLRequest"));
  }

  const onSumbit = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    console.log(e);
    loginPost(id, password);
  };
  return (
    <LoginContainer>
      <TextLogo src={require("../../../imgs/textLogo.png")} />
      <LoginForm onSubmit={onSumbit}>
        <InputBox>
          <InputTitle> 학사서비스 ID</InputTitle>
          <Input
            type="text"
            placeholder=" ID를 입력하세요"
            id="id"
            value={id}
            onChange={(e) => setId(e.target.value)}
          />
        </InputBox>
        <InputBox>
          <InputTitle> 학사서비스 PW</InputTitle>
          <Input
            type="password"
            placeholder=" PW를 입력하세요"
            id="pw"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </InputBox>
        <LoginButton>로그인</LoginButton>
      </LoginForm>
    </LoginContainer>
  );
};

export default Login;

const TextLogo = styled.img``;

const LoginContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  width: 1440px;
  height: 900px;
`;

const LoginForm = styled.form`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
`;

const LoginButton = styled.button`
  background-color: #1890ff;
  color: white;
  border-radius: 5px;
  height: 2.5rem;
  width: 5rem;
  font-weight: bold;
`;

const InputBox = styled.div``;

const InputTitle = styled.div``;

const Input = styled.input`
  color: black;
  border: 1px solid rgba(217, 217, 217, 1);
  margin-top: 10px;
  margin-bottom: 20px;
  padding-left: 5px;
  width: 50rem;
  height: 2.5rem;
`;
// "autoLogin=Y&&returnPage=%2Fjsp%2Fsso%2Fip%2Flogin_form.jsp&userAgent=PC%7CMACINTEL%7CCHROME%7C114&relogin=N"
